sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	//function to post a custom object to the server, which updates the record. 
	//when the notes are changed. triggered by angular directive ng-change.
	c.updateNote = function(updateType) {
		if (c.data.title && c.data.note !== ''){
			c.server.get({
				action: 'updateNote',
				noteID: c.data.noteID,
				noteBody: c.data.note,
				noteTitle: c.data.title
			}).then(function(r) {
				//after we update the note, we propogate an event that causes the other widget
				//to get current data from the table so it changes in real time.
				$rootScope.$emit('updatedNote');
			});
		}
	}

	//event listener that gets the note and puts it in it's editable body.
	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'getNote') {
				data.note = {};
				//see other widget for full explanation of server side code.
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			} else if (input.action == 'updateNote') { 
				note.title = input.noteTitle; 
				note.note = input.noteBody; 
				note.update(); 
			}
		}
	}
})();
